import beach from "../images/waldo-beach.jpeg";
import snow from "../images/waldo-snow.jpeg";
import space from "../images/waldo-space.jpeg";
import track from "../images/waldo-track.jpeg";
import {
    getFirestore,
    collection,
    getDocs,
    addDoc,
    getDoc,
    setDoc,
    doc,
  } from "firebase/firestore";
  import {useState} from 'react';

function Leaderboards() {
    const [beachScores, setBeachScores] = useState([]);
    const [snowScores, setSnowScores] = useState([]);
    const [spaceScores, setSpaceScores] = useState([]);
    const [trackScores, setTrackScores] = useState([]);
  //map function at the bottom that will add all the score info from firebase
  //for whatever image in clicked
  const allScores = getFirestore();
  const scoreRef = collection(allScores, "beachScores");
  getDocs(scoreRef)
    .then((snapshot) => {
      let scores = [];
      snapshot.docs.forEach((doc) => {
        scores.push({ ...doc.data(), id: doc.id });
      });
      console.log("beachScores", beachScores);
      setBeachScores(beachScores)
    })
    .catch((err) => {
      console.log(err.message);
    });

  return (
    <div>
      <div>Leaderboards!</div>
      <ul className="leaderboardDisplay">
        <li className="leaderboardBeach">
          <img src={beach} alt="Beach"></img>
        </li>

        <li className="leaderboardSnow">
          <img src={snow} alt="Snow"></img>
        </li>

        <li className="leaderboardSpace">
          <img src={space} alt="Space"></img>
        </li>

        <li className="leaderboardTrack">
          <img src={track} alt="Track"></img>
        </li>
      </ul>
      <div className="board">
        <div>Name</div>
        <div>Title</div>
        <div>{beachScores.map((arr) => {
            return (
               <div
               key={arr.id}

               >
                   <div>
                       <div>{arr.name}</div>
                   </div>



               </div> 
            )
        })}</div>
      </div>
    </div>
  );
}

export default Leaderboards;
