import { Link } from "react-router-dom";
import App from "../App";
import { useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

function NavBar(props) {
  const [navText, setNavText] = useState("Where's Waldo");
  let location = useLocation();
  //add Timer ad bottom of set navBar
  const setNavBar = () => {
    return (
      <nav className="nav">
        <Link to="/waldo">
          <button>Return to Home</button>
        </Link>

        <div className="characterContainer">
          <div className="waldoImageAndName">
            <img></img>
            <div>Waldo</div>
          </div>
          <div className="odlawImageAndName">
            <img></img>
            <div>Odlaw</div>
          </div>
          <div className="wizardImageAndName">
            <img></img>
            <div>Wizard</div>
          </div>
        </div>
      </nav>
    );
  };
  useEffect(() => {
    if (location.pathname === "/waldo") {
      setNavText("Where's Waldo");
    } else if (location.pathname === "/beach") {
      setNavText(setNavBar);
    }
  }, [location]);

  return <nav className="nav">{navText}</nav>;
}

export default NavBar;
