import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./Home";
import React, { useState } from "react";
import NavBar from "./components/NavBar";
import Beach from "./components/Beach";
import Snow from "./components/Snow";
import Space from "./components/Space";
import Track from "./components/Track";
import Leaderboards from "./components/Leaderboards";

const RouteSwitch = () => {
  const [time, setTime] = useState(0);
  const [timerOn, setTimerOn] = useState(false);
  const [allowPopup, setAllowPopup] = useState(false);

  const unBlurImage = (e) => {

      setTimerOn(true);
      setAllowPopup(true); //allows people to click image to make guesses
      // const beachImg = document.querySelector(".beachContainer");
      // beachImg.children[1].className = "beachBig"; //removed the blur
      setStartButtonAndNotificationText("");
      const mapBig = document.querySelector(".mapBig");


      // setTimerOn(true);
      // setAllowPopup(true); //allows people to click image to make guesses
      // const snowImg = document.querySelector(".snowContainer").children[1];
      // snowImg.className = "snowBig"; //removed the blur
      // setStartButtonAndNotificationText("");

      // setTimerOn(true);
      // setAllowPopup(true); //allows people to click image to make guesses
      // const trackImg = document.querySelector(".trackContainer").children[1];
      // trackImg.className = "trackBig"; //removed the blur
      // setStartButtonAndNotificationText("");

      // setTimerOn(true);
      // setAllowPopup(true); //allows people to click image to make guesses
      // const spaceImg = document.querySelector(".spaceContainer").children[1];
      // spaceImg.className = "spaceBig"; //removed the blur
      // setStartButtonAndNotificationText("");

  };

  const [startButtonAndNotificationText, setStartButtonAndNotificationText] =
    useState(<button className="startTimerButton">Start!</button>);

  return (
    <BrowserRouter>
      <NavBar
        time={time}
        setTime={setTime}
        timerOn={timerOn}
        setTimerOn={setTimerOn}
        allowPopup={allowPopup}
        setAllowPopup={setAllowPopup}
        startButtonAndNotificationText={startButtonAndNotificationText}
        setStartButtonAndNotificationText={setStartButtonAndNotificationText}
      />
      <Routes>
        <Route path="/wheres-waldo" element={<Home />} />
        <Route
          path="/beach"
          element={
            <Beach
              time={time}
              setTime={setTime}
              timerOn={timerOn}
              allowPopup={allowPopup}
              setAllowPopup={setAllowPopup}
              setTimerOn={setTimerOn}
              startButtonAndNotificationText={startButtonAndNotificationText}
              setStartButtonAndNotificationText={
                setStartButtonAndNotificationText
              }
            />
          }
        />
        <Route
          path="/snow"
          element={
            <Snow
              time={time}
              setTime={setTime}
              timerOn={timerOn}
              setTimerOn={setTimerOn}
              allowPopup={allowPopup}
              setAllowPopup={setAllowPopup}
            />
          }
        />
        <Route
          path="/space"
          element={
            <Space
              time={time}
              setTime={setTime}
              timerOn={timerOn}
              setTimerOn={setTimerOn}
              allowPopup={allowPopup}
              setAllowPopup={setAllowPopup}
            />
          }
        />
        <Route
          path="/track"
          element={
            <Track
              time={time}
              setTime={setTime}
              timerOn={timerOn}
              setTimerOn={setTimerOn}
              allowPopup={allowPopup}
              setAllowPopup={setAllowPopup}
            />
          }
        />
        <Route path="/leaderboards" element={<Leaderboards />} />
      </Routes>
    </BrowserRouter>
  );
};

export default RouteSwitch;
