    setNavText("Where's Waldo");
    setTimerOn = false;
  }

  //add Timer ad bottom of set navBar
  const setNavBar = () => {
    return (
      <nav className="nav">
        <Link to="/waldo">
          <button className="returnHome" onClick={clearInterval}>Return to Home</button>
        </Link>

        <div className="characterContainer">
          <div className="waldoImageAndName">
            <img src={waldo} alt="Waldo"></img>
            <div>Waldo</div>
          </div>
          <div className="odlawImageAndName">
            <img src={odlaw} alt="Odlaw"></img>
            <div>Odlaw</div>
          </div>
          <div className="whitebeardImageAndName">
            <img src={whitebeard} alt="Whitebeard"></img>
            <div>Whitebeard</div>
          </div>
        </div>
        <div className="timer">
          <div className="minutes">{("0" + Math.floor((time / 60000) % 60)).slice(-2)}</div>
          <div>:</div>
          <div className="seconds">{("0" + Math.floor((time / 1000) % 60)).slice(-2)}</div>
          <div>:</div>
          <div className="milli">{("0" + ((time / 10) % 100)).slice(-2)}</div>
        </div>
      </nav>
    );
  };
  useEffect(() => { 
