import beach from "../images/waldo-beach.jpeg";
import { useEffect, useState } from "react";
import { getFirestore, collection, getDocs, addDoc, connectFirestoreEmulator } from "firebase/firestore";

function Beach() {
  const [popup, setPopup] = useState(true);
  const [popupText, setPopupText] = useState("");
  const [circle, setCircle] = useState("");
  const [circleCoordinates, setCircleCoordinates] = useState([]);
  const [validateCharacter, setValidateCharacter] = useState(false);
  const [notificationText, setNotificationText] = useState("")

  const allScores = getFirestore();
  const scoreRef = collection(allScores, 'scores');
  getDocs(scoreRef)
    .then((snapshot) => {
      let scores = [];
      snapshot.docs.forEach((doc) => {
        scores.push({ ...doc.data(), id: doc.id });
      });

    })
    .catch((err) => {
      console.log(err.message);
    });

    const characterCheckRef = getFirestore();
    const characterRef = collection(characterCheckRef, 'characterCheck');


  const popUpScreen = (e) => {
    setPopup((prevPopup) => !prevPopup);
    // const rect = e.target.getBoundingClientRect();
    // console.log("rect" , rect)
    // console.log(e.pageX)
    if (popup) {
      const circleX = { left: e.pageX };
      const circleY = { top: e.pageY };
      setCircleCoordinates([circleX, circleY])
      const popupX = { left: e.pageX + 10 } ;
      const popupY = { top: e.pageY + 10 };
      console.log("before funct" , circleX, circleY)
      setPopupText(
        <div className="popup" style={{ ...popupX, ...popupY }}>
          <div onClick={switchValidateCharacter} className="waldoText">Waldo</div>
          <div onClick={switchValidateCharacter} className="odlawText">Odlaw</div>
          <div onClick={switchValidateCharacter} className="whitebeardText">Whitebeard</div>
        </div>
      );
      setCircle(
          <div className="circle" style={{...circleX, ...circleY}}>

          </div>
      )
    } else {
      setPopupText("");
      setCircle("")
    }
  };

  const switchValidateCharacter = () => { //switch between true and false to check for character on every click
    setValidateCharacter((prevValue) => !prevValue);
  } 

  useEffect(() => {
      if (circleCoordinates.length === 2) {
     if (circleCoordinates[0].left < 909 && circleCoordinates[0].left > 871 && circleCoordinates[1].top < 371 && circleCoordinates[1].top > 326){
         setNotificationText("Found Waldo!")
     }
     if (circleCoordinates[0].left < 177 && circleCoordinates[0].left > 135 && circleCoordinates[1].top < 351 && circleCoordinates[1].top > 307){
        setNotificationText("Found Odlaw!")
    }
    if (circleCoordinates[0].left < 408 && circleCoordinates[0].left > 370 && circleCoordinates[1].top < 351 && circleCoordinates[1].top > 310){
        setNotificationText("Found Whitebeard!")
    }
      }
  }, [validateCharacter])

  return (
    <div>
      <div className="beachBig" onClick={popUpScreen}>
        {popupText}
        {circle}
        <img src={beach} alt="Beach"></img>
      </div>
    </div>
  );
}

export default Beach;
