import beach from "../images/waldo-beach.jpeg";
import {useState} from 'react'

function Beach() {
    const [popup, setPopup] = useState("")
  const popUpScreen = (e) => {

    console.log(e)
    console.log("X" , e.pageX)
    console.log("Y" , e.clientY)
    setPopup(
        <div className="popup" style={{left: e.pageX}, {top: e.pageY}}>
            <div>Waldo</div>
            <div>Odlaw</div>
            <div>Whitebeard</div>
        </div>
    )
  };
  return (
    <div>
      <div className="beachBig" onClick={popUpScreen}>{popup}
        <img src={beach} alt="Beach"></img>

      </div>
    </div>
  );
}

export default Beach;
