import beach from "../images/waldo-beach.jpeg";
import { useEffect, useState } from "react";
import { getFirestore, collection, getDocs, addDoc, connectFirestoreEmulator } from "firebase/firestore";

function Beach() {
  const [popup, setPopup] = useState(true);
  const [popupText, setPopupText] = useState("");
  const [circle, setCircle] = useState("")
  const allScores = getFirestore();
  const scoreRef = collection(allScores, 'scores');
  getDocs(scoreRef)
    .then((snapshot) => {
      let scores = [];
      snapshot.docs.forEach((doc) => {
        scores.push({ ...doc.data(), id: doc.id });
      });

    })
    .catch((err) => {
      console.log(err.message);
    });

    const characterCheckRef = getFirestore();
    const characterRef = collection(characterCheckRef, 'characterCheck');


  const popUpScreen = (e) => {
    setPopup((prevPopup) => !prevPopup);
    // const rect = e.target.getBoundingClientRect();
    // console.log("rect" , rect)
    // console.log(e.pageX)
    if (popup) {
      const circleX = { left: e.pageX } ;
      const circleY = { top: e.pageY };
      const popupX = { left: e.pageX + 10 } ;
      const popupY = { top: e.pageY + 10 };
      console.log(circleX, circleY)
      setPopupText(
        <div className="popup" style={{ ...popupX, ...popupY }}>
          <div onClick={validateWaldo}className="waldoText">Waldo</div>
          <div className="odlawText">Odlaw</div>
          <div className="whitebeardText">Whitebeard</div>
        </div>
      );
      setCircle(
          <div className="circle" style={{...circleX, ...circleY}}>

          </div>
      )
    } else {
      setPopupText("");
      setCircle("")
    }
  };

  const validateWaldo = () => {
    getDocs(characterRef).then((snapshot) => {
        let characters = [];
        snapshot.docs.forEach((doc) => {
            characters.push({...doc.data(), id: doc.id})
        })
        console.log("waldo" , characters[0].waldo)
    })
    .catch((err) => {
        console.log(err.message);
      });
  }

  return (
    <div>
      <div className="beachBig" onClick={popUpScreen}>
        {popupText}
        {circle}
        <img src={beach} alt="Beach"></img>
      </div>
    </div>
  );
}

export default Beach;
