import beach from "../images/waldo-beach.jpeg";
import { useEffect, useState } from "react";
import { getFirestore, collection, getDocs, addDoc } from "firebase/firestore";

function Beach() {
  const [popup, setPopup] = useState(true);
  const [popupText, setPopupText] = useState("");
  const allScores = getFirestore();
  const scoreRef = collection(allScores, 'scores');
  getDocs(scoreRef)
    .then((snapshot) => {
      let scores = [];
      snapshot.docs.forEach((doc) => {
        scores.push({ ...doc.data(), id: doc.id });
      });
      console.log(scores);
    })
    .catch((err) => {
      console.log(err.message);
    });


  const popUpScreen = (e) => {
    setPopup((prevPopup) => !prevPopup);
    console.log(popup);
    if (popup) {
      const x = { left: e.pageX };
      const y = { top: e.pageY };
      setPopupText(
        <div className="popup" style={{ ...x, ...y }}>
          <div className="waldoText">Waldo</div>
          <div className="odlawText">Odlaw</div>
          <div className="whitebeardText">Whitebeard</div>
        </div>
      );
    } else {
      setPopupText("");
    }
  };
  const validateCharacter = () => {
   console.log()
  }

  return (
    <div>
      <div className="beachBig" onClick={popUpScreen}>
        {popupText}
        <img src={beach} alt="Beach"></img>
      </div>
    </div>
  );
}

export default Beach;
