import beach from "../images/waldo-beach.jpeg";
import { useEffect, useState } from "react";
import { getFirestore, collection, getDocs, addDoc } from "firebase/firestore";

function Beach() {
  const [popup, setPopup] = useState(true);
  const [popupText, setPopupText] = useState("");
  const [circle, setCircle] = useState("")
  const allScores = getFirestore();
  const scoreRef = collection(allScores, 'scores');
  getDocs(scoreRef)
    .then((snapshot) => {
      let scores = [];
      snapshot.docs.forEach((doc) => {
        scores.push({ ...doc.data(), id: doc.id });
      });

    })
    .catch((err) => {
      console.log(err.message);
    });

    const characterCheckRef = getFirestore();
    const characterRef = collection(characterCheckRef, 'characterCheck');


  const popUpScreen = (e) => {
    setPopup((prevPopup) => !prevPopup);
    let rect = e.target.getBoundingClientRect();
    console.log("rect" , rect)
    if (popup) {
      const x = { left: e.pageX } ;
      const y = { top: e.pageY };

      setPopupText(
        <div className="popup" style={{ ...x, ...y }}>
          <div onClick={validateCharacter}className="waldoText">Waldo</div>
          <div className="odlawText">Odlaw</div>
          <div className="whitebeardText">Whitebeard</div>
        </div>
      );
      setCircle(
          <div className="circle" style={{...x, ...y}}>

          </div>
      )
    } else {
      setPopupText("");
      setCircle("")
    }
  };
  function getRelativeCoordinates (event, referenceElement) {

    const position = {
      x: event.pageX,
      y: event.pageY
    };

    const offset = {
      left: referenceElement.offsetLeft,
      top: referenceElement.offsetTop
    };

    let reference = referenceElement.offsetParent;

    while(reference){
      offset.left += reference.offsetLeft;
      offset.top += reference.offsetTop;
      reference = reference.offsetParent;
    }

    return { 
      x: position.x - offset.left,
      y: position.y - offset.top,
    }; 

  }
  const validateCharacter = () => {
    getDocs(characterRef).then((snapshot) => {
        let characters = [];
        snapshot.docs.forEach((doc) => {
            characters.push({...doc.data(), id: doc.id})
        })
        console.log("waldo" , characters[0].waldo)
    })
    .catch((err) => {
        console.log(err.message);
      });
  }

  return (
    <div>
      <div className="beachBig" onClick={popUpScreen}>
        {popupText}
        {circle}
        <img src={beach} alt="Beach"></img>
      </div>
    </div>
  );
}

export default Beach;
