    document.body.textContent =
    "clientX: " + event.clientX +
    " - clientY: " + event.clientY;
    console.log(event.clientX)
  };
  return (
    <div>
      <div className="beachBig" onClick={popUpScreen}>{popup}
        <img src={beach} alt="Beach"></img>

      </div>
    </div>
  );
}

export default Beach;
